INSERT INTO "authors" ("id", "name", "email", "bio", "avatar_url", "website_url", "social_media", "is_verified", "status", "created_at", "updated_at") VALUES (1, 'Jane Austen', 'jane.austen@classic.com', 'Jane Austen was an English novelist known primarily for her six major novels, which interpret, critique and comment upon the British landed gentry at the end of the 18th century.', NULL, NULL, NULL, false, 'active', '2025-07-31T11:47:19.180Z', '2025-07-31T11:47:19.180Z');
INSERT INTO "authors" ("id", "name", "email", "bio", "avatar_url", "website_url", "social_media", "is_verified", "status", "created_at", "updated_at") VALUES (2, 'F. Scott Fitzgerald', 'fitzgerald@classic.com', 'F. Scott Fitzgerald was an American novelist, essayist, screenwriter, and short-story writer, best known for his novels depicting the flamboyance and excess of the Jazz Age.', NULL, NULL, NULL, false, 'active', '2025-07-31T11:47:19.537Z', '2025-07-31T11:47:19.537Z');
INSERT INTO "authors" ("id", "name", "email", "bio", "avatar_url", "website_url", "social_media", "is_verified", "status", "created_at", "updated_at") VALUES (3, 'Harper Lee', 'harper.lee@classic.com', 'Harper Lee was an American novelist best known for her 1960 novel To Kill a Mockingbird. It won the 1961 Pulitzer Prize and has become a classic of modern American literature.', NULL, NULL, NULL, false, 'active', '2025-07-31T11:47:19.948Z', '2025-07-31T11:47:19.948Z');
INSERT INTO "authors" ("id", "name", "email", "bio", "avatar_url", "website_url", "social_media", "is_verified", "status", "created_at", "updated_at") VALUES (4, 'George Orwell', 'george.orwell@classic.com', 'George Orwell was an English novelist, essayist, journalist and critic. His work is characterised by lucid prose, biting social criticism, opposition to totalitarianism, and outspoken support of democratic socialism.', NULL, NULL, NULL, false, 'active', '2025-07-31T11:47:20.356Z', '2025-07-31T11:47:20.356Z');
INSERT INTO "authors" ("id", "name", "email", "bio", "avatar_url", "website_url", "social_media", "is_verified", "status", "created_at", "updated_at") VALUES (5, 'J.R.R. Tolkien', 'tolkien@classic.com', 'J.R.R. Tolkien was an English writer, poet, philologist, and academic, best known as the author of the high fantasy works The Hobbit and The Lord of the Rings.', NULL, NULL, NULL, false, 'active', '2025-07-31T11:47:20.957Z', '2025-07-31T11:47:20.957Z');
INSERT INTO "authors" ("id", "name", "email", "bio", "avatar_url", "website_url", "social_media", "is_verified", "status", "created_at", "updated_at") VALUES (6, 'Sun Tzu', 'sun.tzu@classic.com', 'Sun Tzu was a Chinese general, military strategist, writer, and philosopher who lived in the Eastern Zhou period of ancient China.', NULL, NULL, NULL, false, 'active', '2025-07-31T11:47:21.234Z', '2025-07-31T11:47:21.234Z');
INSERT INTO "authors" ("id", "name", "email", "bio", "avatar_url", "website_url", "social_media", "is_verified", "status", "created_at", "updated_at") VALUES (7, 'Morgan Housel', 'morgan.housel@finance.com', 'Morgan Housel is a partner at The Collaborative Fund and a former columnist at The Motley Fool and The Wall Street Journal.', NULL, NULL, NULL, false, 'active', '2025-07-31T11:47:21.587Z', '2025-07-31T11:47:21.587Z');
INSERT INTO "authors" ("id", "name", "email", "bio", "avatar_url", "website_url", "social_media", "is_verified", "status", "created_at", "updated_at") VALUES (8, 'James Clear', 'james.clear@selfhelp.com', 'James Clear is an American author, entrepreneur, and photographer. He is the author of the #1 New York Times bestseller Atomic Habits.', NULL, NULL, NULL, false, 'active', '2025-07-31T11:47:21.973Z', '2025-07-31T11:47:21.973Z');
INSERT INTO "authors" ("id", "name", "email", "bio", "avatar_url", "website_url", "social_media", "is_verified", "status", "created_at", "updated_at") VALUES (9, 'Matt Haig', 'matt.haig@fiction.com', 'Matt Haig is an English novelist and journalist. He has written both fiction and non-fiction books for children and adults, often in the speculative fiction genre.', NULL, NULL, NULL, false, 'active', '2025-07-31T11:47:22.411Z', '2025-07-31T11:47:22.411Z');
