INSERT INTO "blog_posts" ("id", "title", "slug", "excerpt", "content", "author_id", "author_name", "status", "featured", "category", "tags", "read_time", "views_count", "likes_count", "comments_count", "seo_title", "seo_description", "seo_keywords", "published_at", "created_at", "updated_at") VALUES (2, 'Top 10 Digital Reading Tips for 2024', 'top-10-digital-reading-tips-2024', 'Master the art of digital reading with these essential tips for better comprehension and retention.', '
            <h2>Top 10 Digital Reading Tips for 2024</h2>
            <p>Digital reading has become the norm, but it requires different strategies than traditional reading. Here are our top tips:</p>
            <ol>
              <li><strong>Adjust Your Screen Settings</strong> - Use warm lighting and appropriate brightness</li>
              <li><strong>Take Regular Breaks</strong> - Follow the 20-20-20 rule (every 20 minutes, look at something 20 feet away for 20 seconds)</li>
              <li><strong>Use Annotation Tools</strong> - Highlight and take notes to improve retention</li>
              <li><strong>Choose the Right Font</strong> - Use fonts designed for screen reading like Open Sans or Georgia</li>
              <li><strong>Optimize Line Spacing</strong> - Increase line spacing for better readability</li>
              <li><strong>Use Reading Mode</strong> - Eliminate distractions with reading mode</li>
              <li><strong>Practice Active Reading</strong> - Ask questions and make connections</li>
              <li><strong>Set Reading Goals</strong> - Track your progress and set achievable targets</li>
              <li><strong>Join Reading Communities</strong> - Discuss books with others</li>
              <li><strong>Mix Digital and Print</strong> - Use both formats for different types of content</li>
            </ol>
            <p>Remember, the key to successful digital reading is finding what works best for you!</p>
          ', NULL, 'ReadnWin Team', 'published', true, 'reading-tips', '["reading tips","digital reading","productivity","2024"]', 8, 10, 0, 0, 'Top 10 Digital Reading Tips for 2024 - Improve Your Reading Experience', 'Master digital reading with these essential tips for better comprehension, retention, and overall reading experience in 2024.', '["digital reading tips","reading strategies","ebook reading","reading productivity"]', '2025-08-01T17:31:12.127Z', '2025-08-01T17:31:12.127Z', '2025-08-05T23:08:14.659Z');
INSERT INTO "blog_posts" ("id", "title", "slug", "excerpt", "content", "author_id", "author_name", "status", "featured", "category", "tags", "read_time", "views_count", "likes_count", "comments_count", "seo_title", "seo_description", "seo_keywords", "published_at", "created_at", "updated_at") VALUES (1, 'Welcome to ReadnWin Blog', 'welcome-to-readnwin-blog', 'Discover the world of digital reading and how it can transform your learning experience.', '
            <h2>Welcome to ReadnWin!</h2>
            <p>We''re excited to launch our blog where we''ll share insights about digital reading, book recommendations, and tips for making the most of your reading experience.</p>
            <p>In this blog, you''ll find:</p>
            <ul>
              <li>Book reviews and recommendations</li>
              <li>Reading tips and strategies</li>
              <li>Digital reading technology updates</li>
              <li>Author interviews and insights</li>
            </ul>
            <p>Stay tuned for regular updates and happy reading!</p>
          ', NULL, 'ReadnWin Team', 'published', true, 'general', '["welcome","introduction","reading"]', 3, 7, 0, 0, 'Welcome to ReadnWin Blog - Your Digital Reading Journey', 'Discover the world of digital reading with ReadnWin. Get book recommendations, reading tips, and insights into the future of reading.', '["digital reading","ebooks","reading tips","book recommendations"]', '2025-08-01T17:31:11.765Z', '2025-08-01T17:31:11.765Z', '2025-08-10T19:26:08.890Z');

