INSERT INTO "books" ("id", "title", "subtitle", "author_id", "category_id", "isbn", "description", "short_description", "cover_image_url", "sample_pdf_url", "ebook_file_url", "format", "language", "pages", "publication_date", "publisher", "price", "original_price", "cost_price", "weight_grams", "dimensions", "stock_quantity", "low_stock_threshold", "is_featured", "is_bestseller", "is_new_release", "status", "seo_title", "seo_description", "seo_keywords", "view_count", "created_at", "updated_at", "inventory_enabled", "delivery_type", "is_digital", "is_physical", "unlimited_stock") VALUES (2, 'The Great Gatsby', 'A Novel', 2, 1, NULL, 'The Great Gatsby is a 1925 novel by American writer F. Scott Fitzgerald. Set in the Jazz Age on Long Island, near New York City, the novel depicts first-person narrator Nick Carraway''s interactions with mysterious millionaire Jay Gatsby and Gatsby''s obsession to reunite with his former lover, Daisy Buchanan.', NULL, 'https://images.unsplash.com/photo-1541963463532-d68292c34b19?w=400&h=600&fit=crop&crop=center', NULL, NULL, 'ebook', 'en', 180, '1925-04-09T23:00:00.000Z', NULL, '12.99', NULL, NULL, NULL, NULL, 1500, 10, true, true, false, 'published', NULL, NULL, NULL, 0, '2025-07-31T11:47:23.632Z', '2025-07-31T11:47:23.632Z', false, 'instant', true, false, false);
