-- Data for table: contact_faqs
INSERT INTO "contact_faqs" ("id", "question", "answer", "order_index", "is_active", "created_at", "updated_at") VALUES (1, 'How do I get started with ReadnWin?', 'Simply sign up for a free account and start exploring our vast library of books. You can read on any device and sync your progress across platforms.', 1, true, '2025-08-05T21:38:02.867Z', '2025-08-05T21:38:02.867Z');
INSERT INTO "contact_faqs" ("id", "question", "answer", "order_index", "is_active", "created_at", "updated_at") VALUES (2, 'What types of books are available?', 'We offer a comprehensive collection including fiction, non-fiction, academic texts, self-help books, and much more. Our library is constantly growing with new releases.', 2, true, '2025-08-05T21:38:02.867Z', '2025-08-05T21:38:02.867Z');
INSERT INTO "contact_faqs" ("id", "question", "answer", "order_index", "is_active", "created_at", "updated_at") VALUES (3, 'Can I read offline?', 'Yes! You can download books to read offline. Simply tap the download button on any book and it will be available for offline reading.', 3, true, '2025-08-05T21:38:02.867Z', '2025-08-05T21:38:02.867Z');
INSERT INTO "contact_faqs" ("id", "question", "answer", "order_index", "is_active", "created_at", "updated_at") VALUES (4, 'How much does ReadnWin cost?', 'We offer both free and premium plans. The free plan includes access to thousands of books, while premium unlocks our entire library and advanced features.', 4, true, '2025-08-05T21:38:02.867Z', '2025-08-05T21:38:02.867Z');
INSERT INTO "contact_faqs" ("id", "question", "answer", "order_index", "is_active", "created_at", "updated_at") VALUES (5, 'How do I cancel my subscription?', 'You can cancel your subscription anytime from your account settings. There are no cancellation fees and you''ll continue to have access until the end of your billing period.', 5, true, '2025-08-05T21:38:02.867Z', '2025-08-05T21:38:02.867Z');
INSERT INTO "contact_faqs" ("id", "question", "answer", "order_index", "is_active", "created_at", "updated_at") VALUES (6, 'Is my reading data private?', 'Absolutely. We take your privacy seriously. Your reading history and personal data are encrypted and never shared with third parties without your explicit consent.', 6, true, '2025-08-05T21:38:02.867Z', '2025-08-05T21:38:02.867Z');

