INSERT INTO "public_pages" ("id", "page_type", "content", "created_at", "updated_at") VALUES (1, 'about_us', '{"cta":{"title":"Join the Reading Revolution","description":"Start your journey with ReadnWin and discover a world of knowledge, imagination, and growth.","primaryButton":"Get Started Free","secondaryButton":"Learn More"},"hero":{"title":"About ReadnWin","subtitle":"Revolutionizing the way people read, learn, and grow"},"team":[{"bio":"Former publishing executive with 15+ years in digital reading innovation. Passionate about making literature accessible to everyone.","name":"Sarah Johnson","role":"CEO & Founder","image":"https://picsum.photos/seed/team-sarah/300/300","twitter":"#","linkedin":"#"},{"bio":"PhD in Computer Science with expertise in AI and machine learning. Leads our technology innovation and platform development.","name":"Dr. Michael Chen","role":"CTO","image":"https://picsum.photos/seed/team-michael/300/300","twitter":"#","linkedin":"#"},{"bio":"Former literary agent and editor. Curates our book collection and ensures quality content for our readers.","name":"Emma Rodriguez","role":"Head of Content","image":"https://picsum.photos/seed/team-emma/300/300","twitter":"#","linkedin":"#"},{"bio":"Award-winning UX designer focused on creating intuitive reading experiences across all devices.","name":"David Wilson","role":"Head of Design","image":"https://picsum.photos/seed/team-david/300/300","twitter":"#","linkedin":"#"}],"stats":[{"label":"Active Readers","number":"50K+"},{"label":"Books Available","number":"100K+"},{"label":"Reader Satisfaction","number":"95%"},{"label":"Support Available","number":"24/7"}],"story":{"title":"Our Story","timeline":[{"year":"2019","title":"Founded","description":"Started with a vision to democratize reading"},{"year":"2021","title":"Growth","description":"Reached 10,000 active readers"},{"year":"2023","title":"Innovation","description":"Launched AI-powered reading recommendations"},{"year":"2024","title":"Expansion","description":"Global platform with 50,000+ readers"}],"description":"ReadnWin was born from a simple observation: while technology was advancing rapidly, the way we read and access literature remained largely unchanged."},"values":[{"icon":"ri-book-open-line","title":"Accessibility","description":"Making reading accessible to everyone, regardless of background or ability."},{"icon":"ri-lightbulb-line","title":"Innovation","description":"Continuously innovating to enhance the reading experience with cutting-edge technology."},{"icon":"ri-heart-line","title":"Community","description":"Building a global community of readers who share knowledge and inspire each other."},{"icon":"ri-shield-check-line","title":"Quality","description":"Maintaining the highest standards in content curation and platform reliability."}],"mission":{"title":"Our Mission","features":["Unlimited Access","Global Community"],"description":"<p>At ReadnWin, we believe that reading is the foundation of personal growth and societal progress. Our mission is to make quality literature accessible to everyone, everywhere.</p>"}}', '2025-08-02T17:23:50.768Z', '2025-08-02T18:10:21.407Z');
INSERT INTO "public_pages" ("id", "page_type", "content", "created_at", "updated_at") VALUES (2, 'contact_us', '{"cta":{"title":"Ready to Get Started?","description":"Join thousands of readers who are already enjoying the ReadnWin experience.","primaryButton":"Start Reading Free","secondaryButton":"Learn More"},"faqs":[{"answer":"Simply sign up for a free account and start exploring our vast library of books. You can read on any device and sync your progress across platforms.","question":"How do I get started with ReadnWin?"},{"answer":"We offer a comprehensive collection including fiction, non-fiction, academic texts, self-help books, and much more. Our library is constantly growing with new releases.","question":"What types of books are available?"},{"answer":"Yes! You can download books to read offline. Simply tap the download button on any book and it will be available for offline reading.","question":"Can I read offline?"},{"answer":"We offer both free and premium plans. The free plan includes access to thousands of books, while premium unlocks our entire library and advanced features.","question":"How much does ReadnWin cost?"},{"answer":"You can cancel your subscription anytime from your account settings. There are no cancellation fees and you''ll continue to have access until the end of your billing period.","question":"How do I cancel my subscription?"},{"answer":"Absolutely. We take your privacy seriously. Your reading history and personal data are encrypted and never shared with third parties without your explicit consent.","question":"Is my reading data private?"}],"form":{"title":"Send us a Message","subjects":["General Inquiry","Technical Support","Account Issues","Billing Questions","Feature Request","Partnership","Press/Media","Other"],"description":"Fill out the form below and we''ll get back to you as soon as possible."},"hero":{"title":"Contact Us","subtitle":"We''d love to hear from you. Get in touch with our team for any questions."},"office":{"hours":"Monday - Friday: 9:00 AM - 6:00 PM","title":"Visit Our Office","address":"123 Reading Street, Book City, BC 12345","parking":"Free parking available in our building","description":"Located in the heart of Book City, our headquarters is where the magic happens. Come visit us and see how we''re revolutionizing the reading experience."},"contactMethods":[{"icon":"ri-mail-line","title":"Email Us","action":"mailto:hello@readnwin.com","contact":"hello@readnwin.com","description":"Get in touch with our support team"},{"icon":"ri-phone-line","title":"Call Us","action":"tel:+15551234567","contact":"+1 (555) 123-4567","description":"Speak with our customer service"},{"icon":"ri-message-3-line","title":"Live Chat","action":"#","contact":"Available 24/7","description":"Chat with us in real-time"},{"icon":"ri-map-pin-line","title":"Visit Us","action":"#","contact":"123 Reading Street, Book City, BC 12345","description":"Our headquarters location"}]}', '2025-08-02T17:23:50.768Z', '2025-08-02T18:11:12.674Z');

